from typing import List
from collections import defaultdict, deque
class TrieNode:
    def __init__(self):
        self.children = defaultdict(TrieNode)
        self.isWord = False
class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word):
        node = self.root

        for w in word:
            node = node.children[w]
        node.isWord = True

class Solution:
    def findWords(self, board, words):

        def dfs(i, j, node, path):
            if node.isWord:
                res.append(path)
                node.isWord = False

            if i < 0 or i >= len(board) or j < 0 or j >= len(board[0]) or board[i][j] == '#':
                return

            c = board[i][j]
            node = node.children.get(c) #if there's no key, return None
            # node = node.children[c]  #if there's no key, default value return
            if not node: return

            board[i][j] = '#'

            for x, y in [(i+1, j), (i, j+1), (i-1, j), (i, j-1)]:
                dfs(x, y, node, path + c)
            board[i][j] = c


        trie = Trie()
        for word in words:
            trie.insert(word)

        res = []
        for i in range(len(board)):
            for j in range(len(board[0])):
                dfs(i, j, trie.root, '')

        return res


