class Solution:
    def findWords(self, board: List[List[str]], words: List[str]) -> List[str]:
        def helper(i, j, w, idx):

            if idx == len(w): return True
            if i < 0 or i >= len(board) or j < 0 or j >= len(board[0]) or w[idx] != board[i][j] or visited.get((i, j)):
                return False

            visited[(i, j)] = True
            for x, y in [(i+1, j), (i, j+1), (i-1, j), (i, j-1)]:
                ret = helper(x, y, w, idx+1)
                if ret: break

            visited[(i, j)] = False
            return ret

        if not words or not board: return []
        res = set()
        for w in words:
            for i in range(len(board)):
                for j in range(len(board[0])):
                    if board[i][j] == w[0]:
                        visited = {}
                        if helper(i, j, w, 0):
                            res.add(w)

        return list(res)

