class Solution2:
    def findWords(self, board, words):
        def wordS(i, j, w, pos=0):
            # print(f"[wordS]  i={i}, j={j}, pos={pos}")
            if pos == len(w):
                # print(f"return True pos {pos} == len(word) {len(words)}, visited={visited}")
                return True

            if i < 0 or i == len(board) or j < 0 or j == len(board[0]) or visited.get((i, j)) or w[pos] != board[i][
                j]:
                # print(f"return False. visited={visited}")
                return False

            visited[(i, j)] = True
            for rowOffset, colOffset in [(0, 1), (1, 0), (0, -1), (-1, 0)]:
                # print(f"   for - rowOffset={rowOffset}, colOffset={colOffset}")
                res = wordS(i + rowOffset, j + colOffset, w, pos + 1)
                if res:
                    # print(f" res={res}, break")
                    break
            visited[(i, j)] = False

            # print(f"  endoffunc, return res - visited={visited}, res={res}")
            return res

        visited = {}
        rtn = []
        for w in words:
            flag = 0
            for i in range(len(board)):
                for j in range(len(board[0])):
                    # print(f"***i={i}, j={j}, w={w}")
                    if board[i][j] == w[0]:
                        if wordS(i, j, w):
                            rtn.append(w)
                            flag = 1
                            break
                if flag == 1: break

        return rtn

    from typing import List
    def findWords2(self, board: List[List[str]], words: List[str]) -> List[str]:

        trie = {}
        for w in words:
            t = trie
            for c in w:
                if c not in t:
                    t[c] = {}
                t = t[c]
            t['#'] = '#'

        print(f"t={t}, trie={trie}")
        self.res = set()
        self.used = [[False] * len(board[0]) for _ in range(len(board))]
        for i in range(len(board)):
            for j in range(len(board[0])):
                self.find(board, i, j, trie, '')
        return list(self.res)

    def find(self, board, i, j, trie, pre):
        if '#' in trie:
            self.res.add(pre)
        if i < 0 or i >= len(board) or j < 0 or j >= len(board[0]):
            return
        if not self.used[i][j] and board[i][j] in trie:
            self.used[i][j] = True
            self.find(board, i + 1, j, trie[board[i][j]], pre + board[i][j])
            self.find(board, i, j + 1, trie[board[i][j]], pre + board[i][j])
            self.find(board, i - 1, j, trie[board[i][j]], pre + board[i][j])
            self.find(board, i, j - 1, trie[board[i][j]], pre + board[i][j])
            self.used[i][j] = False


